// Validate and parse Eventbrite webhook payload
const body = $input.all()[0].body;

if (!body || !body.config || !body.api_url) {
  throw new Error('Invalid Eventbrite webhook payload');
}

// Extract order ID from API URL
const apiUrl = body.api_url;
const orderIdMatch = apiUrl.match(/orders\/([^\/]+)\//); 
if (!orderIdMatch) {
  throw new Error('Could not extract order ID from API URL');
}

const orderId = orderIdMatch[1];
const eventType = body.config.action || 'unknown';

console.log(`Processing Eventbrite webhook: ${eventType} for order ${orderId}`);

return [{
  orderId,
  eventType,
  apiUrl,
  webhookPayload: body,
  timestamp: new Date().toISOString()
}];

// Process Eventbrite order and map to Brevo format with state mapping
const orderData = $('Fetch Order Details').item.json;
const eventData = $('Fetch Event Details').item.json;
const venueData = $('Fetch Venue Details').item.json;

// Australian state mapping function
const stateMapping = {
  'sydney': 'NSW', 'newcastle': 'NSW', 'wollongong': 'NSW', 'nsw': 'NSW', 'new south wales': 'NSW',
  'melbourne': 'VIC', 'geelong': 'VIC', 'ballarat': 'VIC', 'vic': 'VIC', 'victoria': 'VIC',
  'brisbane': 'QLD', 'gold coast': 'QLD', 'sunshine coast': 'QLD', 'qld': 'QLD', 'queensland': 'QLD',
  'perth': 'WA', 'fremantle': 'WA', 'bunbury': 'WA', 'wa': 'WA', 'western australia': 'WA',
  'adelaide': 'SA', 'mount gambier': 'SA', 'sa': 'SA', 'south australia': 'SA',
  'hobart': 'TAS', 'launceston': 'TAS', 'tas': 'TAS', 'tasmania': 'TAS',
  'darwin': 'NT', 'alice springs': 'NT', 'nt': 'NT', 'northern territory': 'NT',
  'canberra': 'ACT', 'act': 'ACT', 'australian capital territory': 'ACT'
};

function mapLocationToState(city, region) {
  const normalizeText = (text) => text?.toLowerCase().trim() || '';

  // Try region first
  const normalizedRegion = normalizeText(region);
  if (stateMapping[normalizedRegion]) {
    return stateMapping[normalizedRegion];
  }

  // Try city
  const normalizedCity = normalizeText(city);
  if (stateMapping[normalizedCity]) {
    return stateMapping[normalizedCity];
  }

  // Try partial matches
  for (const [location, state] of Object.entries(stateMapping)) {
    if (normalizedCity.includes(location) || normalizedRegion.includes(location)) {
      return state;
    }
  }

  return 'Unknown';
}

// Only process placed orders
if (orderData.status !== 'placed') {
  console.log(`Skipping order ${orderData.id} with status: ${orderData.status}`);
  return [];
}

// Get state from venue location
const venueCity = venueData.address?.city || '';
const venueRegion = venueData.address?.region || '';
const customerState = mapLocationToState(venueCity, venueRegion);

// Process attendees (Eventbrite can have multiple attendees per order)
const customers = [];

if (orderData.attendees && orderData.attendees.length > 0) {
  for (const attendee of orderData.attendees) {
    if (attendee.email && attendee.status === 'Attending') {
      customers.push({
        email: attendee.email,
        firstName: attendee.first_name || '',
        lastName: attendee.last_name || '',
        state: customerState,
        eventName: eventData.name?.text || 'Unknown Event',
        eventDate: eventData.start?.utc || '',
        venue: venueData.name || 'Unknown Venue',
        platform: 'Eventbrite',
        orderTotal: orderData.costs?.gross?.value || 0,
        ticketQuantity: orderData.attendees?.length || 1,
        marketingOptIn: true, // Eventbrite doesn't have explicit opt-in data
        orderDate: orderData.created,
        listIds: [3], // Stand Up Sydney list ID
        orderId: orderData.id,
        eventId: eventData.id
      });
    }
  }
} else {
  // Fallback: use order-level data if no attendees
  customers.push({
    email: orderData.email,
    firstName: orderData.first_name || '',
    lastName: orderData.last_name || '',
    state: customerState,
    eventName: eventData.name?.text || 'Unknown Event',
    eventDate: eventData.start?.utc || '',
    venue: venueData.name || 'Unknown Venue',
    platform: 'Eventbrite',
    orderTotal: orderData.costs?.gross?.value || 0,
    ticketQuantity: 1,
    marketingOptIn: true,
    orderDate: orderData.created,
    listIds: [3],
    orderId: orderData.id,
    eventId: eventData.id
  });
}

console.log(`Found ${customers.length} customers to sync to Brevo from Eventbrite order`);
return customers;

// Log sync results for Eventbrite webhook
const results = $input.all();
let successCount = 0;
let errorCount = 0;
const errors = [];

for (const result of results) {
  if (result.json.statusCode && result.json.statusCode >= 400) {
    errorCount++;
    errors.push({
      email: result.json.email || 'unknown',
      error: result.json.message || 'Unknown error',
      statusCode: result.json.statusCode
    });
  } else {
    successCount++;
  }
}

const webhookData = $('Parse Webhook Payload').item.json;

const summary = {
  timestamp: new Date().toISOString(),
  platform: 'Eventbrite',
  eventType: webhookData.eventType,
  orderId: webhookData.orderId,
  totalProcessed: results.length,
  successCount,
  errorCount,
  errors: errors.slice(0, 5), // Limit to first 5 errors
  status: errorCount === 0 ? 'success' : errorCount < results.length / 2 ? 'partial_success' : 'failed'
};

console.log('Eventbrite â†’ Brevo Webhook Sync Summary:', JSON.stringify(summary, null, 2));

return [summary];

