// Error handling and logging
const error = $json.error || $input.first()?.json?.error;
const timestamp = new Date().toISOString();

const errorLog = {
  timestamp,
  workflowName: 'Humanitix Complete Data Extraction',
  error: error || 'Unknown error occurred',
  context: {
    node: $workflow.active.node,
    executionId: $workflow.id,
    runId: $workflow.runId
  },
  severity: 'high',
  impact: 'Partner invoicing data extraction failed'
};

console.error('Extraction Error:', errorLog);

return [{ json: errorLog }];

