// Manual event extraction configuration
// Accepts webhook payload with specific event IDs or date ranges

const payload = $json;

// Extract configuration from webhook payload
const config = {
  extractionType: payload.extractionType || 'specific-events', // 'specific-events', 'date-range', 'all-events'
  eventIds: payload.eventIds || [], // Array of specific event IDs
  dateRange: payload.dateRange || {
    start: new Date(new Date().setDate(new Date().getDate() - 7)).toISOString(), // Last 7 days
    end: new Date().toISOString()
  },
  includeFinancials: payload.includeFinancials !== false, // Default true
  includeCustomers: payload.includeCustomers !== false, // Default true
  generateInvoice: payload.generateInvoice || false,
  outputFormat: payload.outputFormat || 'detailed', // 'summary', 'detailed', 'invoice'
  requestedBy: payload.requestedBy || 'manual-trigger',
  priority: payload.priority || 'normal' // 'low', 'normal', 'high', 'urgent'
};

// Validation
if (config.extractionType === 'specific-events' && config.eventIds.length === 0) {
  throw new Error('Event IDs must be provided for specific event extraction');
}

// Performance settings based on priority
const performanceSettings = {
  low: { batchSize: 10, rateLimitDelay: 2000, maxRetries: 2 },
  normal: { batchSize: 25, rateLimitDelay: 1000, maxRetries: 3 },
  high: { batchSize: 50, rateLimitDelay: 500, maxRetries: 5 },
  urgent: { batchSize: 100, rateLimitDelay: 100, maxRetries: 10 }
};

// Output configuration
const outputConfig = {
  summary: {
    includeOrderDetails: false,
    includeCustomerDetails: false,
    includeTicketBreakdown: false
  },
  detailed: {
    includeOrderDetails: true,
    includeCustomerDetails: true,
    includeTicketBreakdown: true
  },
  invoice: {
    includeOrderDetails: true,
    includeCustomerDetails: true,
    includeTicketBreakdown: true,
    generatePDF: true,
    calculatePartnerShare: true
  }
};

return [{
  json: {
    config,
    performance: performanceSettings[config.priority],
    outputSettings: outputConfig[config.outputFormat],
    timestamp: new Date().toISOString(),
    requestId: `manual-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  }
}];

// Extract specific events by ID
const config = $json.config;
const eventIds = config.eventIds;

// Create individual requests for each event ID
const eventRequests = eventIds.map(eventId => ({
  json: {
    eventId,
    extractionConfig: config,
    requestType: 'specific-event'
  }
}));

return eventRequests;

// Process events from different sources
const eventData = $json;
const config = $node['Configure Manual Extraction'].json.config;

// Handle different event data structures
let events = [];

if (eventData.data && Array.isArray(eventData.data)) {
  // From date range fetch
  events = eventData.data;
} else if (eventData.id) {
  // From specific event fetch
  events = [eventData];
} else {
  // Single event object
  events = [eventData];
}

// Process each event
const processedEvents = events.map(event => ({
  id: event.id,
  name: event.name,
  description: event.description,
  startDate: event.startDate,
  endDate: event.endDate,
  venue: event.venue,
  status: event.status,
  ticketsSold: event.ticketsSold || 0,
  grossRevenue: event.grossRevenue || 0,
  url: event.url,
  createdAt: event.createdAt,
  updatedAt: event.updatedAt,
  extractionConfig: config
}));

return processedEvents.map(event => ({ json: event }));

// Process complete event data with all related information
const eventData = $node['Split Events'].json;
const ticketsResponse = $node['Fetch Tickets'].json;
const ordersResponse = $node['Fetch Orders'].json;
const config = eventData.extractionConfig;
const outputSettings = $node['Configure Manual Extraction'].json.outputSettings;

// Process ticket types
const ticketTypes = (ticketsResponse.data || []).map(ticket => ({
  id: ticket.id,
  name: ticket.name,
  price: ticket.price || 0,
  quantityAvailable: ticket.quantityAvailable || 0,
  quantitySold: ticket.quantitySold || 0,
  status: ticket.status
}));

// Process orders with comprehensive financial data
const orders = (ordersResponse.data || []).map(order => {
  const subtotal = order.subtotal || 0;
  const fees = {
    humanitix: order.totals?.humanitixFee || 0,
    booking: order.totals?.bookingFee || 0,
    passedOn: order.totals?.passedOnFee || 0,
    absorbed: order.totals?.absorbedFee || 0,
    amex: order.totals?.amexFee || 0,
    zip: order.totals?.zipFee || 0
  };

  const discounts = order.totals?.discounts || 0;
  const refunds = order.totals?.refunds || 0;
  const rebates = order.totals?.rebates || 0;
  const total = order.total || 0;

  // Calculate partner share
  const partnerShare = subtotal - discounts - refunds - rebates - fees.passedOn;

  const orderData = {
    id: order.id,
    orderName: order.orderName,
    eventId: eventData.id,
    customer: {
      email: order.email,
      firstName: order.firstName,
      lastName: order.lastName,
      mobile: order.mobile,
      location: order.location || 'AU'
    },
    tickets: order.tickets || [],
    financials: {
      subtotal,
      fees,
      discounts,
      refunds,
      rebates,
      total,
      partnerShare
    },
    status: order.status,
    financialStatus: order.financialStatus,
    paymentGateway: order.paymentGateway,
    createdAt: order.createdAt,
    completedAt: order.completedAt
  };

  // Filter data based on output settings
  if (!outputSettings.includeCustomerDetails) {
    delete orderData.customer;
  }
  if (!outputSettings.includeTicketBreakdown) {
    delete orderData.tickets;
  }

  return orderData;
});

// Calculate event summary
const eventSummary = {
  totalOrders: orders.length,
  totalRevenue: orders.reduce((sum, order) => sum + order.financials.total, 0),
  totalPartnerShare: orders.reduce((sum, order) => sum + order.financials.partnerShare, 0),
  totalFees: orders.reduce((sum, order) => sum + Object.values(order.financials.fees).reduce((a, b) => a + b, 0), 0),
  totalDiscounts: orders.reduce((sum, order) => sum + order.financials.discounts, 0),
  totalRefunds: orders.reduce((sum, order) => sum + order.financials.refunds, 0),
  averageOrderValue: orders.length > 0 ? orders.reduce((sum, order) => sum + order.financials.total, 0) / orders.length : 0
};

// Create complete event data
const completeEventData = {
  event: {
    id: eventData.id,
    name: eventData.name,
    description: eventData.description,
    startDate: eventData.startDate,
    endDate: eventData.endDate,
    venue: eventData.venue,
    status: eventData.status,
    ticketsSold: eventData.ticketsSold,
    grossRevenue: eventData.grossRevenue,
    url: eventData.url,
    createdAt: eventData.createdAt,
    updatedAt: eventData.updatedAt
  },
  ticketTypes,
  orders: outputSettings.includeOrderDetails ? orders : [],
  summary: eventSummary,
  extractionMetadata: {
    extractionType: config.extractionType,
    extractedAt: new Date().toISOString(),
    requestId: $node['Configure Manual Extraction'].json.requestId,
    requestedBy: config.requestedBy,
    outputFormat: config.outputFormat,
    priority: config.priority
  }
};

return [{ json: completeEventData }];

// Generate final extraction report
const allEventData = $input.all();
const config = $node['Configure Manual Extraction'].json.config;
const requestId = $node['Configure Manual Extraction'].json.requestId;

// Create comprehensive extraction report
const extractionReport = {
  requestInfo: {
    requestId,
    extractionType: config.extractionType,
    requestedBy: config.requestedBy,
    priority: config.priority,
    outputFormat: config.outputFormat,
    requestedAt: $node['Configure Manual Extraction'].json.timestamp,
    completedAt: new Date().toISOString()
  },

  extractionSummary: {
    totalEventsExtracted: allEventData.length,
    totalOrders: allEventData.reduce((sum, event) => sum + event.json.summary.totalOrders, 0),
    totalRevenue: allEventData.reduce((sum, event) => sum + event.json.summary.totalRevenue, 0),
    totalPartnerShare: allEventData.reduce((sum, event) => sum + event.json.summary.totalPartnerShare, 0),
    totalFees: allEventData.reduce((sum, event) => sum + event.json.summary.totalFees, 0),
    totalDiscounts: allEventData.reduce((sum, event) => sum + event.json.summary.totalDiscounts, 0),
    totalRefunds: allEventData.reduce((sum, event) => sum + event.json.summary.totalRefunds, 0),
    averageOrderValue: allEventData.length > 0 ? allEventData.reduce((sum, event) => sum + event.json.summary.averageOrderValue, 0) / allEventData.length : 0
  },

  eventDetails: allEventData.map(event => ({
    eventId: event.json.event.id,
    eventName: event.json.event.name,
    eventDate: event.json.event.startDate,
    venue: event.json.event.venue?.name || 'TBD',
    status: event.json.event.status,
    summary: event.json.summary,
    ticketTypes: event.json.ticketTypes.length,
    orders: event.json.orders.length
  })),

  // Include full event data if detailed output
  fullEventData: config.outputFormat === 'detailed' ? allEventData.map(event => event.json) : undefined,

  // Generate invoice data if requested
  invoiceData: config.generateInvoice ? {
    invoiceId: `MANUAL-${requestId}`,
    generatedAt: new Date().toISOString(),
    totalAmount: allEventData.reduce((sum, event) => sum + event.json.summary.totalPartnerShare, 0),
    currency: 'AUD',
    events: allEventData.map(event => ({
      eventId: event.json.event.id,
      eventName: event.json.event.name,
      eventDate: event.json.event.startDate,
      partnerShare: event.json.summary.totalPartnerShare,
      orderCount: event.json.summary.totalOrders
    }))
  } : undefined,

  dataQuality: {
    extractionSuccess: true,
    dataCompleteness: 100,
    processingTime: new Date().getTime() - new Date($node['Configure Manual Extraction'].json.timestamp).getTime(),
    apiCallsUsed: allEventData.length * 3, // events + tickets + orders per event
    rateLimitRespected: true
  }
};

return [{ json: extractionReport }];

