// Partner-specific extraction configuration
// This workflow extracts data for specific partners/promoters

const partners = [
  {
    id: 'stand-up-sydney',
    name: 'Stand Up Sydney',
    eventIds: [], // Will be populated dynamically
    email: 'admin@standupsydney.com',
    invoiceFrequency: 'monthly',
    sharePercentage: 74.3 // Based on analysis
  },
  {
    id: 'comedy-club-partner',
    name: 'Comedy Club Partner',
    eventIds: [],
    email: 'partner@comedyclub.com',
    invoiceFrequency: 'weekly',
    sharePercentage: 75.0
  }
];

// Extract parameters
const extractionParams = {
  dateRange: {
    start: new Date(new Date().setDate(new Date().getDate() - 30)).toISOString(), // Last 30 days
    end: new Date().toISOString()
  },
  includeRefunds: true,
  includeDiscounts: true,
  generateInvoices: true,
  emailInvoices: false // Set to true to auto-email
};

// Partner filtering criteria
const filterCriteria = {
  byEventName: ['Stand Up Sydney', 'Comedy Night', 'Live Comedy'],
  byVenue: ['iD Comedy Club', 'Comedy Store', 'Enmore Theatre'],
  byPromoter: ['Stand Up Sydney'],
  minRevenue: 100 // Minimum revenue threshold
};

return partners.map(partner => ({
  json: {
    partner,
    extractionParams,
    filterCriteria,
    timestamp: new Date().toISOString()
  }
}));

// Filter events for specific partner
const partnerConfig = $json.partner;
const filterCriteria = $json.filterCriteria;
const allEvents = $node['Fetch Partner Events'].json.data || [];

// Filter events based on partner criteria
const partnerEvents = allEvents.filter(event => {
  // Filter by event name
  const matchesEventName = filterCriteria.byEventName.some(name => 
    event.name.toLowerCase().includes(name.toLowerCase())
  );

  // Filter by venue
  const matchesVenue = filterCriteria.byVenue.some(venue => 
    event.venue?.name?.toLowerCase().includes(venue.toLowerCase())
  );

  // Filter by minimum revenue
  const meetsMinRevenue = (event.grossRevenue || 0) >= filterCriteria.minRevenue;

  return matchesEventName || matchesVenue || meetsMinRevenue;
});

return partnerEvents.map(event => ({
  json: {
    ...event,
    partnerConfig,
    isPartnerEvent: true
  }
}));

// Process partner-specific order data with custom calculations
const eventData = $node['Split Partner Events'].json;
const ordersResponse = $node['Fetch Partner Orders'].json;
const partnerConfig = eventData.partnerConfig;

// Process orders with partner-specific calculations
const orders = (ordersResponse.data || []).map(order => {
  const subtotal = order.subtotal || 0;
  const fees = {
    humanitix: order.totals?.humanitixFee || 0,
    booking: order.totals?.bookingFee || 0,
    passedOn: order.totals?.passedOnFee || 0,
    absorbed: order.totals?.absorbedFee || 0,
    amex: order.totals?.amexFee || 0,
    zip: order.totals?.zipFee || 0
  };

  const discounts = order.totals?.discounts || 0;
  const refunds = order.totals?.refunds || 0;
  const rebates = order.totals?.rebates || 0;
  const total = order.total || 0;

  // Calculate partner share based on partner-specific percentage
  const basePartnerShare = subtotal - discounts - refunds - rebates - fees.passedOn;
  const partnerShare = basePartnerShare * (partnerConfig.sharePercentage / 100);
  const platformShare = basePartnerShare - partnerShare;

  return {
    id: order.id,
    orderName: order.orderName,
    eventId: eventData.id,
    eventName: eventData.name,
    customer: {
      name: `${order.firstName || ''} ${order.lastName || ''}`.trim(),
      email: order.email,
      mobile: order.mobile,
      location: order.location || 'AU'
    },
    tickets: order.tickets || [],
    financials: {
      subtotal,
      fees,
      discounts,
      refunds,
      rebates,
      total,
      partnerShare,
      platformShare,
      partnerPercentage: partnerConfig.sharePercentage
    },
    status: order.status,
    financialStatus: order.financialStatus,
    paymentGateway: order.paymentGateway,
    createdAt: order.createdAt,
    completedAt: order.completedAt
  };
});

// Create partner-specific event summary
const partnerEventSummary = {
  partnerId: partnerConfig.id,
  partnerName: partnerConfig.name,
  event: {
    id: eventData.id,
    name: eventData.name,
    startDate: eventData.startDate,
    venue: eventData.venue,
    status: eventData.status
  },
  orders,
  summary: {
    totalOrders: orders.length,
    totalRevenue: orders.reduce((sum, order) => sum + order.financials.total, 0),
    totalPartnerShare: orders.reduce((sum, order) => sum + order.financials.partnerShare, 0),
    totalPlatformShare: orders.reduce((sum, order) => sum + order.financials.platformShare, 0),
    totalFees: orders.reduce((sum, order) => sum + Object.values(order.financials.fees).reduce((a, b) => a + b, 0), 0),
    totalDiscounts: orders.reduce((sum, order) => sum + order.financials.discounts, 0),
    totalRefunds: orders.reduce((sum, order) => sum + order.financials.refunds, 0),
    averageOrderValue: orders.length > 0 ? orders.reduce((sum, order) => sum + order.financials.total, 0) / orders.length : 0,
    partnerSharePercentage: partnerConfig.sharePercentage
  },
  processedAt: new Date().toISOString()
};

return [{ json: partnerEventSummary }];

// Generate comprehensive partner invoice
const partnerEvents = $input.all();
const partnerConfig = partnerEvents[0]?.json.partnerName ? partnerEvents[0].json : null;

if (!partnerConfig) {
  return [{ json: { error: 'No partner events found' } }];
}

// Calculate totals across all partner events
const totals = {
  totalEvents: partnerEvents.length,
  totalOrders: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalOrders, 0),
  totalRevenue: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalRevenue, 0),
  totalPartnerShare: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalPartnerShare, 0),
  totalPlatformShare: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalPlatformShare, 0),
  totalFees: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalFees, 0),
  totalDiscounts: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalDiscounts, 0),
  totalRefunds: partnerEvents.reduce((sum, event) => sum + event.json.summary.totalRefunds, 0)
};

// Generate partner invoice
const partnerInvoice = {
  invoiceHeader: {
    invoiceId: `${partnerConfig.partnerId}-${new Date().getTime()}`,
    invoiceDate: new Date().toISOString(),
    dueDate: new Date(new Date().setDate(new Date().getDate() + 30)).toISOString(),
    partnerId: partnerConfig.partnerId,
    partnerName: partnerConfig.partnerName,
    partnerEmail: partnerConfig.partnerEmail || 'admin@standupsydney.com',
    invoiceFrequency: partnerConfig.invoiceFrequency || 'monthly',
    currency: 'AUD'
  },

  invoiceSummary: {
    periodStart: $node['Configure Partner Extraction'].json.extractionParams.dateRange.start,
    periodEnd: $node['Configure Partner Extraction'].json.extractionParams.dateRange.end,
    ...totals,
    partnerPayoutAmount: totals.totalPartnerShare,
    partnerSharePercentage: partnerConfig.sharePercentage || 74.3
  },

  eventBreakdown: partnerEvents.map(event => ({
    eventId: event.json.event.id,
    eventName: event.json.event.name,
    eventDate: event.json.event.startDate,
    venue: event.json.event.venue.name,
    eventSummary: event.json.summary,
    orderDetails: event.json.orders.map(order => ({
      orderId: order.id,
      orderName: order.orderName,
      customerName: order.customer.name,
      customerEmail: order.customer.email,
      orderTotal: order.financials.total,
      partnerShare: order.financials.partnerShare,
      platformShare: order.financials.platformShare,
      fees: order.financials.fees,
      discounts: order.financials.discounts,
      refunds: order.financials.refunds,
      status: order.status,
      financialStatus: order.financialStatus,
      purchaseDate: order.createdAt,
      completedDate: order.completedAt
    }))
  })),

  paymentInstructions: {
    paymentMethod: 'Bank Transfer',
    accountDetails: {
      accountName: 'Stand Up Sydney',
      bsb: '123-456',
      accountNumber: '12345678',
      reference: `${partnerConfig.partnerId}-${new Date().getTime()}`
    },
    paymentDueDate: new Date(new Date().setDate(new Date().getDate() + 30)).toISOString()
  },

  invoiceFooter: {
    generatedAt: new Date().toISOString(),
    generatedBy: 'N8N Humanitix Integration',
    version: '2.0',
    dataSource: 'humanitix-api',
    notes: `Invoice generated for ${partnerConfig.partnerName} covering ${totals.totalEvents} events with ${totals.totalOrders} orders.`
  }
};

return [{ json: partnerInvoice }];

