// Mock business data
const businesses = [
  {
    name: 'Test Business',
    email: 'test@example.com',
    phone: '+1234567890'
  }
];

return businesses.map(b => ({json: b}));

// Parse the events from the Humanitix API response
const response = $input.all()[0].json;
console.log('Response keys:', Object.keys(response));
console.log('Total events:', response.total);

// Extract the events array from the response
const events = response.events || [];

// Return each event as a separate item for processing
if (Array.isArray(events) && events.length > 0) {
  console.log(`Processing ${events.length} events`);
  return events.map(event => ({ json: event }));
} else {
  console.log('No events found in response');
  return [];
}

// Transform Humanitix order data to Notion format
const orderResponse = $input.all()[0].json;
const transformedOrders = [];

// Handle the response - could be an array or object with data property
let orders = [];
if (Array.isArray(orderResponse)) {
  orders = orderResponse;
} else if (orderResponse.data && Array.isArray(orderResponse.data)) {
  orders = orderResponse.data;
} else if (orderResponse.orders && Array.isArray(orderResponse.orders)) {
  orders = orderResponse.orders;
}

for (const order of orders) {
  // Extract ticket information
  const ticketTypes = order.tickets?.map(t => t.ticketType?.name || t.name || 'General').join(', ') || 'General Admission';
  const totalQuantity = order.tickets?.reduce((sum, t) => sum + (t.quantity || 1), 0) || 1;

  // Get event info from the previous node
  const eventInfo = $('Parse Events').item.json;

  transformedOrders.push({
    eventId: order.eventId || eventInfo._id,
    orderId: order._id || order.id,
    properties: {
      "Name": {
        title: [{ text: { content: `${order.firstName || order.buyer?.firstName || ''} ${order.lastName || order.buyer?.lastName || ''}`.trim() || 'Unknown' } }]
      },
      "Order ID": {
        rich_text: [{ text: { content: order._id || order.id || 'N/A' } }]
      },
      "Event Name": {
        rich_text: [{ text: { content: eventInfo.name || order.event?.name || 'Unknown Event' } }]
      },
      "Email": {
        email: order.email || order.buyer?.email || null
      },
      "Order Date": {
        date: { start: order.createdAt || order.purchaseDate || new Date().toISOString() }
      },
      "Total Amount": {
        number: parseFloat(order.total || order.amount || 0)
      },
      "Net Sales": {
        number: parseFloat(order.subtotal || order.total || order.amount || 0)
      },
      "Quantity": {
        number: totalQuantity
      },
      "Ticket Type": {
        rich_text: [{ text: { content: ticketTypes } }]
      },
      "Payment Status": {
        rich_text: [{ text: { content: (order.status || 'completed').charAt(0).toUpperCase() + (order.status || 'completed').slice(1) } }]
      },
      "Ticketing Partner": {
        select: { name: "Humanitix" }
      },
      "Event ID": {
        rich_text: [{ text: { content: eventInfo._id || '' } }]
      },
      "Event Date & Time": {
        date: { start: eventInfo.startDate || eventInfo.dates?.[0]?.startDate || new Date().toISOString() }
      }
    }
  });

  // Add optional fields
  const lastOrder = transformedOrders[transformedOrders.length - 1];

  // Add mobile number if available
  if (order.phone) {
    lastOrder.properties["Mobile"] = {
      phone_number: order.phone
    };
  }

  // Add event banner URL if available
  if (eventInfo.bannerImage?.url) {
    lastOrder.properties["Event Banner URL"] = {
      url: eventInfo.bannerImage.url
    };
  }

  // Add discount info if available
  if (order.discountCode) {
    lastOrder.properties["Discount Code"] = {
      rich_text: [{ text: { content: order.discountCode } }]
    };
  }

  if (order.discountAmount) {
    lastOrder.properties["Discount Amount"] = {
      number: parseFloat(order.discountAmount)
    };
  }
}

return transformedOrders.map(order => ({ json: order }));

// Count processed orders
const processedCount = $input.all().length;

return [{
  json: {
    timestamp: new Date().toISOString(),
    message: `Sync completed successfully`,
    ordersProcessed: processedCount,
    status: 'success'
  }
}];

// Configuration for the search
const config = {
  searchQuery: "hostels backpackers Sydney NSW",
  maxResults: 20,
  includeContactInfo: true
};

// Generate Google Maps search URL
const searchUrl = `https://www.google.com/maps/search/${encodeURIComponent(config.searchQuery)}`;

return [{
  json: {
    config,
    searchUrl,
    timestamp: new Date().toISOString()
  }
}];

// Process Firecrawl results
const response = $input.all()[0].json;
const businesses = [];

// Extract business data from the scraped content
if (response.data && response.data.json && response.data.json.businesses) {
  for (const business of response.data.json.businesses) {
    businesses.push({
      businessName: business.name || '',
      address: business.address || '',
      phone: business.phone || '',
      website: business.website || '',
      rating: business.rating || 0,
      reviewCount: business.reviewCount || 0,
      category: business.category || 'Business',
      googleMapsUrl: '',
      email: '', // Will be enriched later
      contactName: '', // Will be enriched later
      searchQuery: $('Search Configuration').item.json.config.searchQuery,
      scrapedAt: new Date().toISOString()
    });
  }
} else if (response.data && response.data.markdown) {
  // Fallback: Parse from markdown content
  const markdownContent = response.data.markdown;
  // Simple regex patterns to extract business info
  const businessMatches = markdownContent.match(/### (.+?)\n/g) || [];

  businessMatches.slice(0, 20).forEach(match => {
    const name = match.replace(/### |\n/g, '');
    businesses.push({
      businessName: name,
      address: '',
      phone: '',
      website: '',
      rating: 0,
      reviewCount: 0,
      category: 'Business',
      googleMapsUrl: '',
      email: '',
      contactName: '',
      searchQuery: $('Search Configuration').item.json.config.searchQuery,
      scrapedAt: new Date().toISOString(),
      needsEnrichment: true
    });
  });
}

// Return each business as a separate item
return businesses.map(business => ({ json: business }));

// Merge enriched contact data
const originalData = $input.all()[0].json;
const enrichedResponse = $('Enrich Contact Info').item.json;

let email = originalData.email;
let contactName = originalData.contactName;
let phone = originalData.phone;

// Extract contact info from enriched data
if (enrichedResponse.data && enrichedResponse.data.json) {
  const enrichedData = enrichedResponse.data.json;

  // Get first email if available
  if (enrichedData.emails && enrichedData.emails.length > 0) {
    email = enrichedData.emails[0];
  }

  // Get first phone if not already available
  if (!phone && enrichedData.phones && enrichedData.phones.length > 0) {
    phone = enrichedData.phones[0];
  }

  // Extract contact name if available
  if (enrichedData.contactName) {
    contactName = enrichedData.contactName;
  }
}

return [{
  json: {
    ...originalData,
    email,
    contactName,
    phone,
    enriched: true
  }
}];

// Generate summary statistics
const allBusinesses = $input.all();

const summary = {
  totalBusinesses: allBusinesses.length,
  businessesWithEmail: allBusinesses.filter(item => item.json.email).length,
  businessesWithPhone: allBusinesses.filter(item => item.json.phone).length,
  businessesWithWebsite: allBusinesses.filter(item => item.json.website).length,
  averageRating: allBusinesses.reduce((sum, item) => sum + (item.json.rating || 0), 0) / allBusinesses.length,
  searchQuery: allBusinesses[0]?.json.searchQuery || 'N/A',
  scrapedAt: new Date().toISOString(),
  enrichmentRate: (allBusinesses.filter(item => item.json.enriched).length / allBusinesses.length * 100).toFixed(1) + '%'
};

return [{ json: summary }];

