// Configuration for the scraping job
const config = {
  searchQuery: $input.all()[0].json.searchQuery || "hostels backpackers Sydney NSW",
  location: $input.all()[0].json.location || "Sydney, NSW, Australia",
  maxResults: $input.all()[0].json.maxResults || 100
};

return [{
  json: {
    config,
    message: `Starting search for: ${config.searchQuery}`
  }
}];

// Import ALL historical data
// Set a very early start date to get everything
const startDate = new Date('2020-01-01'); // Adjust if your data goes back further
const endDate = new Date();

console.log(`Importing ALL historical data from ${startDate.toDateString()} to ${endDate.toDateString()}`);

return [{
  json: {
    startDate: startDate.toISOString(),
    endDate: endDate.toISOString(),
    currentPage: 1,
    hasMore: True
  }
}];

// Handle pagination for getting ALL events
const currentPage = $json.currentPage || 1;
const maxPages = 100; // Safety limit

if (currentPage > maxPages) {
  console.log(`Reached max pages limit (${maxPages})`);
  return [];
}

return [{
  json: {
    ...$json,
    page: currentPage
  }
}];

// Process the events response
const response = $input.all()[0].json;
let events = [];

// Handle different response formats
if (Array.isArray(response)) {
  events = response;
} else if (response.events && Array.isArray(response.events)) {
  events = response.events;
} else if (response.data && Array.isArray(response.data)) {
  events = response.data;
}

console.log(`Found ${events.length} events on page ${$json.page}`);

// Check if there are more pages
const hasMore = events.length === 100; // If we got a full page, there might be more

// Return events and pagination info
return events.map(event => ({
  json: {
    event,
    hasMore,
    nextPage: hasMore ? ($json.page + 1) : null
  }
}));

// Transform ALL orders to Notion format
const orderResponse = $input.all()[0].json;
const eventInfo = $json.event;
const transformedOrders = [];

// Extract orders from response
let orders = [];
if (Array.isArray(orderResponse)) {
  orders = orderResponse;
} else if (orderResponse.orders && Array.isArray(orderResponse.orders)) {
  orders = orderResponse.orders;
} else if (orderResponse.data && Array.isArray(orderResponse.data)) {
  orders = orderResponse.data;
}

console.log(`Processing ${orders.length} orders for event: ${eventInfo.name || eventInfo.title}`);

for (const order of orders) {
  // Extract ticket details
  const ticketTypes = order.tickets?.map(t => 
    t.ticketType?.name || t.type?.name || t.name || 'General'
  ).join(', ') || 'General';

  const totalQuantity = order.tickets?.reduce((sum, t) => 
    sum + (t.quantity || 1), 0
  ) || 1;

  transformedOrders.push({
    orderId: order._id || order.id,
    properties: {
      "Event Name": {
        title: [{ text: { content: eventInfo.name || eventInfo.title || 'Unknown Event' } }]
      },
      "Event Date": eventInfo.date || eventInfo.startDate ? {
        date: { start: eventInfo.date || eventInfo.startDate }
      } : {
        date: { start: new Date().toISOString() }
      },
      "Platform": {
        select: { name: "Humanitix" }
      },
      "Order ID": {
        rich_text: [{ text: { content: order._id || order.id || 'N/A' } }]
      },
      "Customer Name": {
        rich_text: [{ text: { 
          content: `${order.firstName || order.buyer?.firstName || ''} ${order.lastName || order.buyer?.lastName || ''}`.trim() || 'Anonymous'
        } }]
      },
      "Customer Email": order.email || order.buyer?.email ? {
        email: order.email || order.buyer?.email
      } : {
        email: "no-email@example.com"
      },
      "Customer Phone": order.phone || order.buyer?.phone ? {
        phone_number: order.phone || order.buyer?.phone
      } : null,
      "Ticket Types": {
        rich_text: [{ text: { content: ticketTypes } }]
      },
      "Quantity": {
        number: totalQuantity
      },
      "Amount": {
        number: parseFloat(order.total || order.amount || order.price || 0)
      },
      "Currency": {
        select: { name: order.currency || eventInfo.currency || "AUD" }
      },
      "Status": {
        select: { name: order.status || "completed" }
      },
      "Purchase Date": order.createdAt || order.purchaseDate ? {
        date: { start: order.createdAt || order.purchaseDate }
      } : {
        date: { start: new Date().toISOString() }
      },
      "Venue": {
        rich_text: [{ text: { 
          content: eventInfo.venue?.name || eventInfo.location?.name || eventInfo.location || 'Online'
        } }]
      },
      "Last Sync": {
        date: { start: new Date().toISOString() }
      },
      "Notes": {
        rich_text: [{ text: { content: "Historical import - All time data" } }]
      }
    }
  });
}

return transformedOrders.map(order => ({ json: order }));

// Track import progress
const items = $input.all();
const importCount = items.length;

return [{
  json: {
    imported: importCount,
    timestamp: new Date().toISOString(),
    message: `Imported ${importCount} historical orders`
  }
}];

// Validate and fix Google Auth user data
const authUser = $input.first().json;
const profileCheck = $('Check Profile Exists').first().json;

// Check if profile creation is needed
const profileExists = Array.isArray(profileCheck) && profileCheck.length > 0;

if (!profileExists) {
  // Create missing profile data
  const profileData = {
    id: authUser.user_id,
    email: authUser.email,
    full_name: authUser.user_metadata?.full_name || authUser.email.split('@')[0],
    avatar_url: authUser.user_metadata?.avatar_url || null,
    role: 'comedian', // Default role
    created_at: new Date().toISOString(),
    onboarding_completed: false,
    provider: authUser.provider || 'google'
  };

  return {
    needs_profile: true,
    user_data: authUser,
    profile_data: profileData,
    action: 'create_profile',
    debug_info: {
      profile_check_result: profileCheck,
      profile_exists: profileExists
    }
  };
} else {
  return {
    needs_profile: false,
    user_data: authUser,
    existing_profile: profileCheck[0],
    action: 'profile_exists',
    debug_info: {
      profile_check_result: profileCheck,
      profile_exists: profileExists
    }
  };
}

