const eventsResponse = $('Fetch Humanitix Events').first().json || {};
const existingEventsResponse = $('Check Existing Events').first().json || [];
const events = eventsResponse.events || [];

console.log(`Processing ${events.length} events from Humanitix`);

// Build set of existing event IDs in database
const existingEventIds = new Set();
for (const existing of existingEventsResponse) {
  existingEventIds.add(existing.source_id);
}
console.log(`Found ${existingEventIds.size} existing events in database`);

const results = [];
const seenIds = new Set();
let skippedExisting = 0;
let skippedDuplicates = 0;

for (const event of events) {
  if (!event._id) {
    console.log('Skipping event without ID');
    continue;
  }

  // Skip if already exists in database
  if (existingEventIds.has(event._id)) {
    console.log(`Skipping existing event in DB: ${event.name} (${event._id})`);
    skippedExisting++;
    continue;
  }

  // Deduplicate within current batch
  if (seenIds.has(event._id)) {
    console.log(`Skipping duplicate in batch: ${event.name} (${event._id})`);
    skippedDuplicates++;
    continue;
  }
  seenIds.add(event._id);

  console.log(`Processing NEW event: ${event.name} (${event._id})`);

  const eventLocation = event.eventLocation || {};
  const pricing = event.pricing || {};
  const paymentOptions = event.paymentOptions || {};
  const accessibility = event.accessibility || {};
  const affiliateCode = event.affiliateCode || {};

  const eventData = {
    source: 'humanitix',
    source_id: event._id,

    // Core Event Information
    name: event.name || '',
    description: event.description || '',
    slug: event.slug || '',
    url: event.url || '',
    category: event.category || '',
    classification: event.classification || null,

    // User & Organization
    user_id: event.userId || '',
    organiser_id: event.organiserId || '',

    // Status Fields
    public: Boolean(event.public),
    published: Boolean(event.published),
    suspended_sales: Boolean(event.suspendSales),
    marked_as_sold_out: Boolean(event.markedAsSoldOut),

    // Dates & Timing
    start_date: event.startDate,
    end_date: event.endDate,
    timezone: event.timezone || '',
    published_at: event.publishedAt,

    // Capacity & Ticketing
    total_capacity: event.totalCapacity || null,
    currency: event.currency || '',

    // Location Information
    location: event.location || '',
    location_type: eventLocation.type || null,
    venue_name: eventLocation.venueName || '',
    venue_address: eventLocation.address || '',
    venue_lat_lng: eventLocation.latLng || null,
    online_url: eventLocation.onlineUrl || null,
    location_instructions: eventLocation.instructions || '',

    // Images
    banner_image_url: event.bannerImage?.url || null,
    feature_image_url: event.featureImage?.url || null,
    social_image_url: event.socialImage?.url || null,

    // SEO & Marketing
    keywords: event.keywords || [],
    tag_ids: event.tagIds || [],
    affiliate_code: affiliateCode.code || null,

    // Artists & Performers
    artists: event.artists || [],

    // Event Structure
    dates: event.dates || [],
    ticket_types: event.ticketTypes || [],
    packaged_tickets: event.packagedTickets || [],
    additional_questions: event.additionalQuestions || [],

    // Pricing Configuration
    pricing_currency: pricing.currency || null,
    pricing_minimum: pricing.minimum || null,
    pricing_maximum: pricing.maximum || null,
    pricing_fees_included: Boolean(pricing.feesIncluded),
    pricing_payment_plan_enabled: Boolean(pricing.paymentPlanEnabled),

    // Payment Options
    payment_options_cash: Boolean(paymentOptions.cash),
    payment_options_card: Boolean(paymentOptions.card),
    payment_options_paypal: Boolean(paymentOptions.paypal),
    payment_options_afterpay: Boolean(paymentOptions.afterpay),
    payment_options_zip: Boolean(paymentOptions.zip),
    payment_options_invoice: Boolean(paymentOptions.invoice),
    payment_options_complimentary: Boolean(paymentOptions.complimentary),

    // Accessibility
    accessibility_wheelchair_accessible: Boolean(accessibility.wheelchairAccessible),
    accessibility_hearing_loop: Boolean(accessibility.hearingLoop),
    accessibility_companion_card: Boolean(accessibility.companionCard),
    accessibility_audio_description: Boolean(accessibility.audioDescription),
    accessibility_auslan_interpreted: Boolean(accessibility.auslanInterpreted),
    accessibility_captioned: Boolean(accessibility.captioned),
    accessibility_relaxed_performance: Boolean(accessibility.relaxedPerformance),
    accessibility_notes: accessibility.notes || null,

    // System Fields
    created_at: event.createdAt,
    updated_at: event.updatedAt,

    // Legacy/Compatibility Fields (for existing Supabase structure)
    title: event.name || '',
    hero_image_url: event.featureImage?.url || null,
    details: event.description || '',
    promoter_id: event.organiserId || '',
    suspendedsale: Boolean(event.suspendSales),
    markedassoldout: Boolean(event.markedAsSoldOut),
    startdate: event.startDate,
    enddate: event.endDate,
    totalcapacity: event.totalCapacity || null,
    organiserid: event.organiserId || '',
    userid: event.userId || '',
    tags: event.tagIds || [],
    eventlocation: event.eventLocation || null,

    // Raw Data & Metadata
    raw: event,
    updated_at_api: new Date().toISOString()
  };

  results.push({ json: eventData });
}

console.log(`RESULTS: ${results.length} NEW events to insert, ${skippedExisting} already existed, ${skippedDuplicates} duplicates in batch`);
return results;

const items = $('Prepare Events Data (Skip Existing)').all();
const existingSessionsResponse = $('Check Existing Sessions').first().json || [];
const results = [];
const seenSessionIds = new Set();

// Build set of existing session IDs in database
const existingSessionIds = new Set();
for (const existing of existingSessionsResponse) {
  existingSessionIds.add(existing.source_id);
}
console.log(`Found ${existingSessionIds.size} existing sessions in database`);

console.log(`Extracting sessions from ${items.length} NEW events`);
let skippedExisting = 0;
let skippedDuplicates = 0;

for (const item of items) {
  const event = item.json;

  const dates = event.dates || [];

  console.log(`Event ${event.name}: ${dates.length} sessions found`);

  if (dates.length === 0) {
    // Single session event - create one session
    const sessionId = `${event.source_id}-single`;

    // Skip if already exists in database
    if (existingSessionIds.has(sessionId)) {
      console.log(`Skipping existing session in DB: ${sessionId}`);
      skippedExisting++;
      continue;
    }

    // Skip if duplicate in current batch
    if (seenSessionIds.has(sessionId)) {
      console.log(`Skipping duplicate session in batch: ${sessionId}`);
      skippedDuplicates++;
      continue;
    }

    seenSessionIds.add(sessionId);
    console.log(`Creating NEW single session for: ${event.name}`);
    results.push({
      json: {
        source: 'humanitix',
        source_id: sessionId,
        event_source_id: event.source_id,
        event_name: event.name,
        starts_at: event.startdate,
        venue_name: event.location || event.eventlocation?.venue || null,
        status: 'active',
        updated_at: event.updated_at,
        raw: { single_session: true, event_id: event.source_id },
        updated_at_api: new Date().toISOString()
      }
    });
  } else {
    // Multi-session event - create from dates array
    console.log(`Creating sessions for: ${event.name}`);
    dates.forEach((session, index) => {
      if (!session.deleted && !session.disabled) {
        const sessionId = session._id || `${event.source_id}-${index + 1}`;

        // Skip if already exists in database
        if (existingSessionIds.has(sessionId)) {
          console.log(`Skipping existing session in DB: ${sessionId}`);
          skippedExisting++;
          return;
        }

        // Skip if duplicate in current batch
        if (seenSessionIds.has(sessionId)) {
          console.log(`Skipping duplicate session in batch: ${sessionId}`);
          skippedDuplicates++;
          return;
        }

        seenSessionIds.add(sessionId);
        console.log(`Creating NEW session: ${sessionId}`);
        results.push({
          json: {
            source: 'humanitix',
            source_id: sessionId,
            event_source_id: event.source_id,
            event_name: event.name,
            starts_at: session.startDate,
            venue_name: event.location || event.eventlocation?.venue || null,
            status: (session.disabled || session.deleted) ? 'disabled' : 'active',
            updated_at: event.updated_at,
            raw: session,
            updated_at_api: new Date().toISOString()
          }
        });
      }
    });
  }
}

console.log(`RESULTS: ${results.length} NEW sessions to insert, ${skippedExisting} already existed, ${skippedDuplicates} duplicates in batch`);
return results;

// Get all events and prepare for Orders/Tickets fetching
const events = $('Prepare Events Data (Skip Existing)').all();
const eventIds = [];

for (const event of events) {
  eventIds.push({
    eventId: event.json.source_id,
    eventName: event.json.name
  });
}

console.log(`Prepared ${eventIds.length} NEW events for Orders/Tickets fetching`);
return eventIds;

// Process Orders with Complete Data (Skip Existing)
const orders = $('Fetch Orders').first().json.orders || [];
const existingOrdersResponse = $('Check Existing Orders').first().json || [];
const contextData = $('Prepare for Orders/Tickets').item.json;
const processedOrders = [];
const seenOrderIds = new Set();

// Build set of existing order IDs in database
const existingOrderIds = new Set();
for (const existing of existingOrdersResponse) {
  existingOrderIds.add(existing.source_id);
}

console.log(`Processing ${orders.length} orders for event ${contextData.eventName}`);
console.log(`Found ${existingOrderIds.size} existing orders in database`);

let skippedExisting = 0;
let skippedDuplicates = 0;

for (const order of orders) {
  if (!order._id) {
    console.log(`Skipping order without ID`);
    continue;
  }

  // Skip if already exists in database
  if (existingOrderIds.has(order._id)) {
    console.log(`Skipping existing order in DB: ${order._id}`);
    skippedExisting++;
    continue;
  }

  // Deduplicate within current batch
  if (seenOrderIds.has(order._id)) {
    console.log(`Skipping duplicate order in batch: ${order._id}`);
    skippedDuplicates++;
    continue;
  }
  seenOrderIds.add(order._id);

  console.log(`Processing NEW order: ${order._id}`);

  const totals = order.totals || {};
  const purchaseTotals = order.purchaseTotals || {};
  const discounts = order.discounts || {};
  const discountCode = discounts.discountCode || {};
  const autoDiscount = discounts.autoDiscount || {};

  const orderData = {
    source: 'humanitix',
    source_id: order._id,

    // Core Identifiers
    event_source_id: order.eventId || contextData.eventId,
    session_source_id: order.eventDateId,
    user_id: order.userId,
    order_reference: order.reference || order._id,

    // Status Information
    status: order.status,
    financial_status: order.financialStatus,
    manual_order: Boolean(order.manualOrder),
    sales_channel: order.salesChannel,
    is_international_transaction: Boolean(order.isInternationalTransaction),

    // Customer Information
    first_name: order.firstName,
    last_name: order.lastName,
    purchaser_name: `${order.firstName || ''} ${order.lastName || ''}`.trim(),
    purchaser_email: order.email,
    mobile: order.mobile,
    organisation: order.organisation,

    // Business Information
    business_purpose: Boolean(order.businessPurpose),
    business_tax_id: order.businessTaxId,
    business_name: order.businessName,

    // Payment Information
    payment_type: order.paymentType,
    payment_gateway: order.paymentGateway,
    tip_fees: Boolean(order.tipFees),
    currency: order.currency,

    // Access & Marketing
    access_code: order.accessCode,
    organiser_mail_list_opt_in: Boolean(order.organiserMailListOptIn),
    waitlist_offer_id: order.waitlistOfferId,

    // Discount Information
    discount_code_used: discountCode.code || null,
    discount_code_amount_cents: discountCode.discountAmount ? Math.round(discountCode.discountAmount * 100) : 0,
    auto_discount_amount_cents: autoDiscount.discountAmount ? Math.round(autoDiscount.discountAmount * 100) : 0,

    // Donation Fields
    client_donation_cents: order.clientDonation ? Math.round(order.clientDonation * 100) : 0,

    // Core Financial Fields (Order Totals)
    total_cents: totals.total ? Math.round(totals.total * 100) : 0,
    subtotal_cents: totals.subtotal ? Math.round(totals.subtotal * 100) : 0,
    net_sales_cents: totals.netSales ? Math.round(totals.netSales * 100) : 0,
    gross_sales_cents: totals.grossSales ? Math.round(totals.grossSales * 100) : 0,

    // Fee Breakdown (Order Totals)
    humanitix_fee_cents: totals.humanitixFee ? Math.round(totals.humanitixFee * 100) : 0,
    booking_fee_cents: totals.bookingFee ? Math.round(totals.bookingFee * 100) : 0,
    passed_on_fee_cents: totals.passedOnFee ? Math.round(totals.passedOnFee * 100) : 0,
    amex_fee_cents: totals.amexFee ? Math.round(totals.amexFee * 100) : 0,
    zip_fee_cents: totals.zipFee ? Math.round(totals.zipFee * 100) : 0,

    // Tax Fields (Order Totals)
    booking_taxes_cents: totals.bookingTaxes ? Math.round(totals.bookingTaxes * 100) : 0,
    passed_on_taxes_cents: totals.passedOnTaxes ? Math.round(totals.passedOnTaxes * 100) : 0,
    taxes_cents: totals.taxes ? Math.round(totals.taxes * 100) : 0,
    total_taxes_cents: totals.totalTaxes ? Math.round(totals.totalTaxes * 100) : 0,

    // Donation Fields (Order Totals)
    order_client_donation_cents: totals.clientDonation ? Math.round(totals.clientDonation * 100) : 0,
    net_client_donation_cents: totals.netClientDonation ? Math.round(totals.netClientDonation * 100) : 0,
    donation_cents: totals.donation ? Math.round(totals.donation * 100) : 0,
    dgr_donation_cents: totals.dgrDonation ? Math.round(totals.dgrDonation * 100) : 0,

    // Credit and Discount Fields (Order Totals)
    gift_card_credit_cents: totals.giftCardCredit ? Math.round(totals.giftCardCredit * 100) : 0,
    credit_cents: totals.credit ? Math.round(totals.credit * 100) : 0,
    discounts_cents: totals.discounts ? Math.round(totals.discounts * 100) : 0,
    refunds_cents: totals.refunds ? Math.round(totals.refunds * 100) : 0,
    referral_amount_cents: totals.referralAmount ? Math.round(totals.referralAmount * 100) : 0,
    outstanding_amount_cents: totals.outstandingAmount ? Math.round(totals.outstandingAmount * 100) : 0,

    // Purchase Totals (separate from order totals)
    purchase_total_cents: purchaseTotals.total ? Math.round(purchaseTotals.total * 100) : 0,
    purchase_subtotal_cents: purchaseTotals.subtotal ? Math.round(purchaseTotals.subtotal * 100) : 0,
    purchase_net_sales_cents: purchaseTotals.netSales ? Math.round(purchaseTotals.netSales * 100) : 0,
    purchase_gross_sales_cents: purchaseTotals.grossSales ? Math.round(purchaseTotals.grossSales * 100) : 0,
    purchase_humanitix_fee_cents: purchaseTotals.humanitixFee ? Math.round(purchaseTotals.humanitixFee * 100) : 0,
    purchase_booking_fee_cents: purchaseTotals.bookingFee ? Math.round(purchaseTotals.bookingFee * 100) : 0,
    purchase_discounts_cents: purchaseTotals.discounts ? Math.round(purchaseTotals.discounts * 100) : 0,
    purchase_refunds_cents: purchaseTotals.refunds ? Math.round(purchaseTotals.refunds * 100) : 0,

    // Boolean Fields
    fees_included: Boolean(totals.feesIncluded),
    purchase_fees_included: Boolean(purchaseTotals.feesIncluded),

    // Timestamps
    created_at: order.createdAt,
    updated_at: order.updatedAt,
    incomplete_at: order.incompleteAt,
    completed_at: order.completedAt,
    ordered_at: order.completedAt || order.createdAt,

    // Location & Notes
    location: order.location,
    notes: order.notes,

    // Additional Data
    additional_fields: order.additionalFields,

    // Raw Data & Metadata
    raw: order,
    ingested_at: new Date().toISOString(),
    updated_at_api: new Date().toISOString()
  };

  processedOrders.push(orderData);
}

console.log(`RESULTS: ${processedOrders.length} NEW orders to insert, ${skippedExisting} already existed, ${skippedDuplicates} duplicates in batch`);
return processedOrders;

// Process Tickets with Complete Data (Skip Existing)
const tickets = $('Fetch Tickets').first().json.tickets || [];
const existingTicketsResponse = $('Check Existing Tickets').first().json || [];
const contextData = $('Prepare for Orders/Tickets').item.json;
const processedTickets = [];
const seenTicketIds = new Set();

// Build set of existing ticket IDs in database
const existingTicketIds = new Set();
for (const existing of existingTicketsResponse) {
  existingTicketIds.add(existing.source_id);
}

console.log(`Processing ${tickets.length} tickets for event ${contextData.eventName}`);
console.log(`Found ${existingTicketIds.size} existing tickets in database`);

let skippedExisting = 0;
let skippedDuplicates = 0;

for (const ticket of tickets) {
  if (!ticket._id) {
    console.log(`Skipping ticket without ID`);
    continue;
  }

  // Skip if already exists in database
  if (existingTicketIds.has(ticket._id)) {
    console.log(`Skipping existing ticket in DB: ${ticket._id}`);
    skippedExisting++;
    continue;
  }

  // Deduplicate within current batch
  if (seenTicketIds.has(ticket._id)) {
    console.log(`Skipping duplicate ticket in batch: ${ticket._id}`);
    skippedDuplicates++;
    continue;
  }
  seenTicketIds.add(ticket._id);

  console.log(`Processing NEW ticket: ${ticket._id}`);

  const checkIn = ticket.checkIn || {};
  const seatingLocation = ticket.seatingLocation || {};
  const discounts = ticket.discounts || {};
  const discountCode = discounts.discountCode || {};
  const autoDiscount = discounts.autoDiscount || {};

  const ticketData = {
    source: 'humanitix',
    source_id: ticket._id,

    // Core Identifiers
    event_source_id: ticket.eventId || contextData.eventId,
    order_source_id: ticket.orderId,
    session_source_id: ticket.eventDateId,
    ticket_type_id: ticket.ticketTypeId,
    ticket_type_name: ticket.ticketTypeName,
    ticket_number: ticket.number,

    // Attendee Information
    first_name: ticket.firstName,
    last_name: ticket.lastName,
    organisation: ticket.organisation,
    attendee_profile_id: ticket.attendeeProfileId,

    // Pricing Fields (in cents)
    price_cents: ticket.price ? Math.round(ticket.price * 100) : 0,
    net_price_cents: ticket.netPrice ? Math.round(ticket.netPrice * 100) : 0,
    total_cents: ticket.total ? Math.round(ticket.total * 100) : 0,
    discount_cents: ticket.discount ? Math.round(ticket.discount * 100) : 0,
    taxes_cents: ticket.taxes ? Math.round(ticket.taxes * 100) : 0,
    fee_cents: ticket.fee ? Math.round(ticket.fee * 100) : 0,
    passed_on_fee_cents: ticket.passedOnFee ? Math.round(ticket.passedOnFee * 100) : 0,
    absorbed_fee_cents: ticket.absorbedFee ? Math.round(ticket.absorbedFee * 100) : 0,
    dgr_donation_cents: ticket.dgrDonation ? Math.round(ticket.dgrDonation * 100) : 0,

    // Package Information
    package_id: ticket.packageId,
    package_name: ticket.packageName,
    package_group_id: ticket.packageGroupId,
    package_price_cents: ticket.packagePrice ? Math.round(ticket.packagePrice * 100) : 0,

    // Discount Information
    discount_code_used: discountCode.code || null,
    discount_code_amount_cents: discountCode.discountAmount ? Math.round(discountCode.discountAmount * 100) : 0,
    auto_discount_amount_cents: autoDiscount.discountAmount ? Math.round(autoDiscount.discountAmount * 100) : 0,

    // Status and Sales
    status: ticket.status,
    sales_channel: ticket.salesChannel,
    is_donation: Boolean(ticket.isDonation),
    cancelled_at: ticket.cancelledAt,

    // Check-in Information
    checked_in: Boolean(checkIn.checkedIn),
    check_in_status: checkIn.status || null,
    check_in_date: checkIn.checkedInAt || null,
    check_in_location: checkIn.location || null,
    check_in_device: checkIn.device || null,
    check_in_notes: checkIn.notes || null,

    // Seating Information
    seating_map_id: seatingLocation.seatingMapId,
    seating_name: seatingLocation.name,
    seating_section: seatingLocation.section,
    seating_table: seatingLocation.table,
    seating_seat: seatingLocation.seat,
    seating_note: seatingLocation.note,

    // Scanning & Access
    barcode: ticket.qrCodeData?._id || ticket.customScanningCode,
    qr_code_data: ticket.qrCodeData,
    custom_scanning_code: ticket.customScanningCode,
    access_code: ticket.accessCode,

    // Swapping Information
    swapped_from: ticket.swappedFrom,
    swapped_to: ticket.swappedTo,

    // System Fields
    currency: ticket.currency,
    location: ticket.location,
    order_name: ticket.orderName,
    additional_fields: ticket.additionalFields,
    check_in_history: ticket.checkInHistory,

    // Timestamps
    created_at: ticket.createdAt,
    updated_at: ticket.updatedAt,

    // Raw Data
    raw: ticket,
    ingested_at: new Date().toISOString(),
    updated_at_api: new Date().toISOString()
  };

  processedTickets.push(ticketData);
}

console.log(`RESULTS: ${processedTickets.length} NEW tickets to insert, ${skippedExisting} already existed, ${skippedDuplicates} duplicates in batch`);
return processedTickets;
