// Process and analyze competitor events
const scraped_data = $input.all();
const analysis = {
  total_events_found: 0,
  venues_analysis: {},
  price_ranges: [],
  upcoming_events: [],
  competitor_insights: []
};

for (const item of scraped_data) {
  const data = item.json;

  if (data.events && Array.isArray(data.events)) {
    analysis.total_events_found += data.events.length;

    for (const event of data.events) {
      // Venue analysis
      if (event.venue) {
        const venue = event.venue.toLowerCase().trim();
        analysis.venues_analysis[venue] = (analysis.venues_analysis[venue] || 0) + 1;
      }

      // Price analysis
      if (event.price) {
        const priceMatch = event.price.match(/\$([0-9.]+)/);
        if (priceMatch) {
          analysis.price_ranges.push(parseFloat(priceMatch[1]));
        }
      }

      // Upcoming events (within next 30 days)
      if (event.date) {
        const eventDate = new Date(event.date);
        const now = new Date();
        const thirtyDaysFromNow = new Date(now.getTime() + (30 * 24 * 60 * 60 * 1000));

        if (eventDate >= now && eventDate <= thirtyDaysFromNow) {
          analysis.upcoming_events.push({
            title: event.title,
            date: event.date,
            venue: event.venue,
            price: event.price,
            source: data.url
          });
        }
      }
    }
  }
}

// Generate insights
if (analysis.price_ranges.length > 0) {
  const avgPrice = analysis.price_ranges.reduce((a, b) => a + b, 0) / analysis.price_ranges.length;
  const minPrice = Math.min(...analysis.price_ranges);
  const maxPrice = Math.max(...analysis.price_ranges);

  analysis.competitor_insights.push({
    type: 'pricing',
    insight: `Competitor pricing: $${minPrice.toFixed(2)} - $${maxPrice.toFixed(2)}, avg: $${avgPrice.toFixed(2)}`,
    recommendation: avgPrice > 25 ? 'Consider premium pricing strategy' : 'Focus on value proposition'
  });
}

// Top venues
const topVenues = Object.entries(analysis.venues_analysis)
  .sort(([,a], [,b]) => b - a)
  .slice(0, 5)
  .map(([venue, count]) => ({ venue, event_count: count }));

if (topVenues.length > 0) {
  analysis.competitor_insights.push({
    type: 'venues',
    insight: `Top competitor venues: ${topVenues.map(v => v.venue).join(', ')}`,
    recommendation: 'Consider partnership opportunities or competitive analysis'
  });
}

analysis.scraped_at = new Date().toISOString();
analysis.top_venues = topVenues;

return [{ json: analysis }];

