// Split platforms by type for parallel processing
const platforms = $input.first().json || [];

const humanitixPlatforms = [];
const eventbritePlatforms = [];
const otherPlatforms = [];

platforms.forEach(platform => {
  switch(platform.platform) {
    case 'humanitix':
      humanitixPlatforms.push(platform);
      break;
    case 'eventbrite':
      eventbritePlatforms.push(platform);
      break;
    default:
      otherPlatforms.push(platform);
  }
});

return [
  { platforms: humanitixPlatforms, type: 'humanitix' },
  { platforms: eventbritePlatforms, type: 'eventbrite' },
  { platforms: otherPlatforms, type: 'other' }
];

// Process Humanitix event data with smart matching
const platform = $('Split Humanitix Events').first().json.platforms[0];
const eventDetails = $('Fetch Humanitix Event Details').first().json;
const ordersResponse = $('Fetch Humanitix Orders').first().json;

// Calculate tickets sold from capacity
const totalCapacity = eventDetails.totalCapacity || 0;
let ticketsSold = 0;
let ticketsAvailable = totalCapacity;

// Sum up sold tickets from ticket types
if (eventDetails.ticketTypes) {
  eventDetails.ticketTypes.forEach(ticketType => {
    const sold = ticketType.quantity - (ticketType.quantityRemaining || ticketType.quantity);
    ticketsSold += sold;
  });
  ticketsAvailable = totalCapacity - ticketsSold;
}

// Get total orders for gross sales estimate
const totalOrders = ordersResponse.total || 0;
const avgTicketPrice = eventDetails.pricing?.minimumPrice || 0;
const grossSales = ticketsSold * avgTicketPrice;

// Extract venue info for matching
const venueName = eventDetails.eventLocation?.venueName || '';
const eventDate = eventDetails.dates?.[0]?.startDate || eventDetails.startDate;

return {
  // If we have a matched event_id use it, otherwise we'll process for matching
  event_id: platform.event_id,
  matched: !!platform.event_id,

  // Data for matching if needed
  matching_data: {
    platform: 'humanitix',
    external_id: eventDetails._id,
    title: eventDetails.name,
    venue: venueName,
    event_date: eventDate,
    capacity: totalCapacity
  },

  // Ticket sales data
  platform: 'humanitix',
  external_event_id: eventDetails._id,
  tickets_sold: ticketsSold,
  tickets_available: ticketsAvailable,
  gross_sales: grossSales,
  external_url: eventDetails.url,
  platform_data: {
    total_capacity: totalCapacity,
    ticket_types: eventDetails.ticketTypes,
    pricing: eventDetails.pricing,
    total_orders: totalOrders,
    last_sync: new Date().toISOString(),
    event_details: {
      name: eventDetails.name,
      venue: venueName,
      date: eventDate
    }
  }
};

