// Import ALL historical data
// Set a very early start date to get everything
const startDate = new Date('2020-01-01');
const endDate = new Date();

console.log(`Importing ALL historical data from ${startDate.toDateString()} to ${endDate.toDateString()}`);

return [{
  json: {
    startDate: startDate.toISOString(),
    endDate: endDate.toISOString(),
    currentPage: 1,
    hasMore: true
  }
}];

// Handle pagination for getting ALL events
const currentPage = $json.currentPage || 1;
const maxPages = 100; // Safety limit

if (currentPage > maxPages) {
  console.log(`Reached max pages limit (${maxPages})`);
  return [];
}

return [{
  json: {
    ...$json,
    page: currentPage
  }
}];

// Process the events response and initialize orders pagination
const response = $input.all()[0].json;
let events = [];

// Handle different response formats
if (Array.isArray(response)) {
  events = response;
} else if (response.events && Array.isArray(response.events)) {
  events = response.events;
} else if (response.data && Array.isArray(response.data)) {
  events = response.data;
}

console.log(`Found ${events.length} events on page ${$json.page}`);

// Return events with orders pagination initialized
return events.map(event => ({
  json: {
    event,
    ordersPage: 1,
    ordersHasMore: true,
    eventHasMore: events.length === 100,
    nextEventPage: events.length === 100 ? ($json.page + 1) : null
  }
}));

// Orders pagination logic
const ordersPage = $json.ordersPage || 1;
const maxOrdersPages = 50; // Safety limit per event

console.log(`Getting orders page ${ordersPage} for event: ${$json.event?.title || 'Unknown'}`);

if (ordersPage > maxOrdersPages) {
  console.log(`Reached max orders pages limit (${maxOrdersPages}) for event`);
  return [];
}

return [{
  json: {
    ...$json,
    currentOrdersPage: ordersPage
  }
}];

// Process orders response and handle pagination
const orderResponse = $input.all()[0]?.json || {};
let orders = [];

// Extract orders from response
if (Array.isArray(orderResponse)) {
  orders = orderResponse;
} else if (orderResponse.orders && Array.isArray(orderResponse.orders)) {
  orders = orderResponse.orders;
} else if (orderResponse.data && Array.isArray(orderResponse.data)) {
  orders = orderResponse.data;
}

const eventInfo = $json.event || {};
const eventName = eventInfo?.title || eventInfo?.name || 'Unknown Event';
const currentPage = $json.currentOrdersPage || 1;

console.log(`Found ${orders.length} orders on page ${currentPage} for event: ${eventName}`);

// Check if there are more orders pages
const ordersHasMore = orders.length === 100; // Full page = likely more pages

if (orders.length === 0) {
  console.log(`No orders found for event: ${eventName}`);
  return [];
}

// Return orders with pagination info
return orders.map(order => ({
  json: {
    ...order,
    event: eventInfo,
    ordersHasMore,
    nextOrdersPage: ordersHasMore ? (currentPage + 1) : null,
    isLastOrdersPage: !ordersHasMore
  }
}));

// Ultra-safe Transform Orders with CORRECT FINANCIAL FIELDS
const orders = $input.all();
const transformedOrders = [];

console.log('ðŸ”„ Transform Orders: Processing', orders.length, 'orders');

for (let i = 0; i < orders.length; i++) {
  const orderItem = orders[i];
  console.log('ðŸ“¦ Processing order item', i + 1, ':', orderItem);

  try {
    // Handle both direct order data and nested json structures
    let orderData, eventInfo;

    if (orderItem.json && orderItem.json.orders && orderItem.json.eventInfo) {
      // From "Get Event Details" format
      const ordersList = orderItem.json.orders;
      eventInfo = orderItem.json.eventInfo;

      for (let j = 0; j < ordersList.length; j++) {
        orderData = ordersList[j];
        transformedOrders.push(processOrder(orderData, eventInfo, j));
      }
    } else if (orderItem.json && Array.isArray(orderItem.json)) {
      // Array of orders
      for (let j = 0; j < orderItem.json.length; j++) {
        orderData = orderItem.json[j];
        eventInfo = { name: 'Unknown Event', _id: 'unknown', startDate: new Date().toISOString() };
        transformedOrders.push(processOrder(orderData, eventInfo, j));
      }
    } else if (orderItem.json) {
      // Single order
      orderData = orderItem.json;
      eventInfo = { name: 'Unknown Event', _id: 'unknown', startDate: new Date().toISOString() };
      transformedOrders.push(processOrder(orderData, eventInfo, 0));
    }
  } catch (error) {
    console.error('âŒ Error processing order:', error.message);
  }
}

function processOrder(orderData, eventInfo, index) {
  console.log('ðŸ”„ Processing individual order:', orderData?._id || 'unknown');

  try {
    const orderId = orderData?._id || orderData?.id || 'unknown-' + Date.now();
    const eventId = orderData?.eventId || eventInfo?._id || 'unknown-event';

    // Safe customer name extraction
    let customerName = 'Anonymous';
    try {
      const firstName = orderData?.firstName || '';
      const lastName = orderData?.lastName || '';
      customerName = `${firstName} ${lastName}`.trim() || 'Anonymous';
    } catch (e) {
      customerName = 'Anonymous';
    }

    const email = orderData?.email || "no-email@example.com";
    const phone = orderData?.mobile || orderData?.phone || null;

    // FIXED FINANCIAL FIELDS - Use correct data sources
    let grossSales = 0;
    let netSales = 0;
    let discountAmount = 0;
    let discountCode = '';

    try {
      // Use grossSales for Total Amount (original price before discount)
      grossSales = parseFloat(orderData?.totals?.grossSales || orderData?.purchaseTotals?.grossSales || 0);
      if (isNaN(grossSales)) grossSales = 0;

      // Use netSales for Net Sales (amount after discount)
      netSales = parseFloat(orderData?.totals?.netSales || orderData?.purchaseTotals?.netSales || 0);
      if (isNaN(netSales)) netSales = 0;

      // Extract discount information
      discountCode = orderData?.discounts?.discountCode?.code || '';
      discountAmount = parseFloat(orderData?.discounts?.discountCode?.discountAmount || orderData?.totals?.discounts || 0);
      if (isNaN(discountAmount)) discountAmount = 0;

      console.log(`ðŸ’° Financial data - Gross: ${grossSales}, Net: ${netSales}, Discount: ${discountCode} (${discountAmount})`);

    } catch (e) {
      console.error('âš ï¸ Error extracting financial data:', e.message);
    }

    const totalQuantity = 1;

    // Safe date extraction
    let eventDate = new Date().toISOString();
    try {
      eventDate = eventInfo?.startDate || eventInfo?.date || new Date().toISOString();
    } catch (e) {
      eventDate = new Date().toISOString();
    }

    let orderDate = new Date().toISOString();
    try {
      orderDate = orderData?.completedAt || orderData?.createdAt || new Date().toISOString();
    } catch (e) {
      orderDate = new Date().toISOString();
    }

    const status = orderData?.status || orderData?.financialStatus || "completed";
    const ticketTypes = 'General Admission';
    const eventName = eventInfo?.name || eventInfo?.title || 'Unknown Event';

    // Create the order entry with CORRECT field mappings
    const orderEntry = {
      orderId: orderId,
      properties: {
        "Name": {
          title: [{ text: { content: customerName } }]
        },
        "Email": {
          email: email
        },
        "Event Name": {
          rich_text: [{ text: { content: eventName } }]
        },
        "Event ID": {
          rich_text: [{ text: { content: eventId } }]
        },
        "Event Date & Time": {
          date: { start: eventDate }
        },
        "Order ID": {
          rich_text: [{ text: { content: orderId } }]
        },
        // FIXED: Use gross sales for Total Amount (original price)
        "Total Amount": {
          number: grossSales
        },
        // NEW: Add Net Sales field
        "Net Sales": {
          number: netSales
        },
        // NEW: Add Discount Code field
        "Discount Code": {
          rich_text: [{ text: { content: discountCode } }]
        },
        // NEW: Add Discount Amount field
        "Discount Amount": {
          number: discountAmount
        },
        "Quantity": {
          number: totalQuantity
        },
        "Ticket Type": {
          rich_text: [{ text: { content: ticketTypes } }]
        },
        "Payment Status": {
          rich_text: [{ text: { content: status } }]
        },
        "Order Date": {
          date: { start: orderDate }
        },
        "Ticketing Partner": {
          select: { name: "Humanitix" }
        },
        "Created At": {
          date: { start: new Date().toISOString() }
        },
        "Updated At": {
          date: { start: new Date().toISOString() }
        }
      }
    };

    // Add phone if available
    if (phone) {
      orderEntry.properties["Mobile"] = {
        phone_number: phone
      };
    }

    console.log(`âœ… Created order entry for: ${customerName} (Gross: $${grossSales}, Net: $${netSales})`);
    return orderEntry;

  } catch (error) {
    console.error('âŒ Error in processOrder:', error.message);
    return null;
  }
}

console.log(`ðŸŽ¯ Transformed ${transformedOrders.length} orders with correct financial fields`);
return transformedOrders.filter(order => order !== null);

// Debug: Check what Notion returns
